var frp_offlineapp=function(){"use strict";this.storeFormValues=function(e){var t=Array();$("div.person",$(e)).each(function(){var e=$("form",$(this));t.push(e.serializeArray())});localStorage.setItem("form-to-localstorage",JSON.stringify(t));$("#code").html(localStorage.getItem("form-to-localstorage"))}},app=new frp_offlineapp;$("div.people").bind("keyup",function(){app.storeFormValues(this)});